key = ""
word = "111001001001111111111111110001010110101001011110011011000101001011011001101110110010010010111000010111101010110101010001101100110000101111111111111011111001000110110000111110001110011100000010011101110110110111010110011000010100111110111001011011100110010011001011011101011010101011001100101111"


word_list = list(word)

for i in range(len(word_list)):
    if key[i] == '1':
        word_list[i] = '0' if word_list[i] == '1' else '1'
encrypted_word = ''.join(word_list)
print(encrypted_word)
squence = str(encrypted_word)

# Ensure dashes are removed if present
squence = squence.replace("-", "")

# Final sentence to build
sentence = ""

# Total number of 7-bit chunks
num_chunks = len(squence) // 7

for i in range(num_chunks):
    chunk = squence[i * 7:(i + 1) * 7]
    
    # Convert the 7-bit binary string to decimal
    decimal_value = int(chunk, 2)

    # Map 121 to space manually
    if decimal_value == 121:
        letter = " "
    elif 65 <= decimal_value <= 90:
        letter = chr(decimal_value + 32)  # A-Z to a-z
    else:
        letter = " "  # Fallback for unknown characters

    sentence += letter

# Output
print("Decoded letters:", len(sentence))
print("Sentence:", sentence)
